import * as React from "react"
import { Frame, useCycle } from "framer"
import styled, { createGlobalStyle } from "styled-components"

// Open Preview (CMD + P)
// API Reference: https://www.framer.com/api

export function FontLoader() {
    const GlobalStyle = createGlobalStyle`
        @font-face {
            font-family: "HurmeGeometricSans2-Regular";
            font-weight: 400;
            src:
            local('HurmeGeometricSans2-Regular'), 
            local('HurmeGeometricSans2 Regular'), 
            url(/code/fonts/hurmegeosans-no2-400.woff) format("woff");
        }
        @font-face {
            font-family: "HurmeGeometricSans2-Bold";
            font-weight: 700;
            src:
            local('HurmeGeometricSans2-Bold'), 
            local('HurmeGeometricSans2 Bold'), 
            url(/code/fonts/hurmegeosans-no2-700.woff) format("woff");
        }
        @font-face {
            font-family: "HurmeGeometricSans2-SemiBold";
            font-weight: 600;
            src:
            local('HurmeGeometricSans2-SemiBold'), 
            local('HurmeGeometricSans2 SemiBold'), 
            url(/code/fonts/hurmegeosans-no2-600.woff) format("woff");
        }
    `
    return <GlobalStyle />
}
FontLoader.defaultProps = {
    height: 40,
    width: 40,
}
