import * as React from "react"
import { Frame, useCycle, addPropertyControls, ControlType } from "framer"
import { qzYellow, qzCoral, qzInk, qzTeal, qzWhite } from "./Assembly"

// Open Preview (CMD + P)
// API Reference: https://www.framer.com/api

export function Qz_Input(props) {
    return (
        <div>
            <label style={UIInput}>
                <div style={UIInputContent}>
                    <input
                        style={UIInputInput}
                        type={props.inputType}
                        // value={props.value}
                    />
                    <span style={UIInputBorder} />
                </div>
                <span style={UIInputLabel}>{props.label}</span>
            </label>
        </div>
    )
}
Qz_Input.defaultProps = {
    label: "label",
    inputType: "text",
    value: " ",
}
// Add Property Controls
addPropertyControls(Qz_Input, {
    value: {
        title: "Value",
        type: ControlType.String,
        defaultValue: "Enter value",
    },
    label: {
        title: "Label",
        type: ControlType.String,
        defaultValue: "Label",
    },
    inputType: {
        title: "Type",
        type: ControlType.Enum,
        defaultValue: "text",
        options: ["text", "number", "password"],
        optionTitles: ["text", "number", "password"],
    },
})

